    . $this->in_stock . "', '"                                 . $this->unit . "', '"                                . $this->url . "', '"                . $this->queue . "')";                 $db = new Database();        $result = $db->readQuery($query);        if ($result) {            $last_id = mysql_insert_id();            return $this->__construct($last_id);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `product` ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysql_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `product` SET "                . "`product_type` ='" . $this->product_type . "', "                . "`name` ='" . $this->name . "', "                . "`image_name` ='" . $this->image_name . "', "                . "`short_description` ='" . $this->short_description . "', "                . "`description` ='" . $this->description . "', "                . "`price` ='" . $this->price . "', "                                . "`in_stock` ='" . $this->in_stock . "', "                                . "`unit` ='" . $this->unit . "', "                                . "`url` ='" . $this->url . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        $query = 'DELETE FROM `product` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function getProductsById($product) {        $query = 'SELECT * FROM `product` WHERE product_type="' . $product . '"   ORDER BY queue ASC';        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysql_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }  public function allProductsByType($product_type) {        $query = "SELECT * FROM `product` WHERE `product_type`= $product_type ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysql_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function arrange($key, $img) {        $query = "UPDATE `product` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}