<?php/** * Description of TourTypes * * @author U s E r Â¨ */class ProductType {    public $id;    public $name;    public $short_description;    public $description;    public $image_name;    public $queue;    public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `product_type` WHERE `id`=" . $id;            $db = new Database();            $result = mysql_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->name = $result['name'];            $this->short_description= $result['short_description'];            $this->description= $result['description'];            $this->image_name = $result['image_name'];            $this->queue = $result['queue'];            return $this;        }    }    public function create() {        $query = "INSERT INTO `product_type` (`name`,`short_description`,`image_name`,`description`,`queue`) VALUES  ('"                . $this->name . "', '"                . $this->short_description . "', '"                . $this->image_name . "', '"                . $this->description . "', '"                . $this->queue . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            $last_id = mysql_insert_id();            return $this->__construct($last_id);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `product_type` ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysql_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `product_type` SET "                . "`name` ='" . $this->name . "', "                . "`short_description` ='" . $this->short_description . "', "                . "`image_name` ='" . $this->image_name . "', "                . "`description` ='" . $this->description . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        unlink(Helper::getSitePath() . "upload/product_type/" . $this->image_name);              $query = 'DELETE FROM `product_type` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function GetTourTypeById($id) {        $query = "SELECT * FROM `product_type` WHERE `id` = '" . $id . "' ORDER BY `queue` ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysql_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function arrange($key, $img) {        $query = "UPDATE `product_type` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}