f ($handle->processed) {            $info = getimagesize($handle->file_dst_pathname);            $imgName = $handle->file_dst_name;        }    }    $PRODUCT = new Product($_POST['id']);    $PRODUCT->image_name = $_POST['oldImageName'];    $PRODUCT->name = $_POST['name'];    $PRODUCT->short_description = $_POST['short_description'];    $PRODUCT->description = $_POST['description'];        $PRODUCT->price = $_POST['price'];        $PRODUCT->unit = $_POST['unit'];    if(empty($_POST['in_stock'])){              $PRODUCT->in_stock=0;      }    else {               $PRODUCT->in_stock=$_POST['in_stock'];            }        $PRODUCT->update();    $result = ["id" => $_POST['id']];    echo json_encode($result);    exit();}if (isset($_POST['save-data'])) {    foreach ($_POST['sort'] as $key => $img) {        $key = $key + 1;        $PRODUCT = Product::arrange($key, $img);        header('Location: ' . $_SERVER['HTTP_REFERER']);    }}